import React from 'react'
import styled from "styled-components";
const Select = styled.select`
  width: 100%;
  height: 35px;
  background: white;
  color: gray;
  font-size: 20px;

  option {
    width: 100%;
  height: 35px;
  background: white;
  color: gray;
  font-size: 20px;
  }
`;

const Input = styled.input`
  width: 100%;
  padding: 12px 20px;
  margin: 8px 0;
  box-sizing: border-box;
`;

const Radio = styled.input`
&[type="radio"]{

  position: absolute
  &:checked:before{
      content:"";
      font: 17px/1 'Open Sans', sans-serif;
      position:absolute;
      width: 100%;
      height: 100%;
      background:orange;
      border-radius: 100%;
      left: 0;
      top: 0;
    }

}

`;

function Search() {
    return (
        <div>
            <div className="radioinput">
                <input type="radio" name="od" value="Offres" checked />
                <label for="Offres">Offres</label>
                <input type="radio" name="od" value="Demandes" />
                <label for="Demandes">Demandes</label>
            </div>
            <div className="inputs">
                <Select name="pets" id="pet-select">
                    <option value="">Catégoris</option>
                    <option value="Emploi">Emploi</option>
                    <option value="véhicule">véhicule</option>
                    <option value="Mode">Mode</option>
                    <option value="Maison">Maison</option>
                </Select>
                <Input type="text" placeholder="Que recherchez-vous" />
                <Input type="text" placeholder="Saisissez une ville et un rayon" />
            </div>


        </div>
    )
}

export default Search
